function doSubmitSearch(view){
	var str = "";
    if(view == "m"){
        str = document.getElementById('tags-m').value;
    }else{
        str = document.getElementById('tags').value;
    }
	
	if(str==''){
		alert('Please enter search term');
		return false;
	}
	else if(str.match(/([\<])([^\>]{1,})*([\>])/i)!=null)
		{
          alert('Please enter a valid search term');
		  return false;
		}
	else{
		str = str.replace(/\s/g, "+");
		if(str.substr(str.length-1,str.length)=='+'){
			str = str.substr(0,str.length-1);
		}
		window.location.href='https://www.forbesindia.com/search.php?searchtext='+str;
		return false;
	}
}
